header('Content-type:text/html;charset=gbk');
$n = 1000;
$data = array();
for ($i = 0; $i < 1000; $i++)
{
    $data[] = array(
        'comment' => '测试评论_num:' . $i,
        'zhuanti_id' => 22,
        'lvl' => $i + 13,
        'sts' => 'y',
        'passid' => 0,
        'uId' => 0,
        'uName' => '#$#' . randIp(),
        'add_time' => time()
    );
}

function randIp()
{
    return  mt_rand(1, 254) . '.' . mt_rand(1, 254) . '.' . mt_rand(1, 254) . '.' . mt_rand(1, 234);
}

$sql = array();
foreach ($data as $key => $val)
{
    $sql[] =  "('{$val['comment']}', '{$val['zhuanti_id']}', '{$val['lvl']}', '{$val['sts']}', '{$val['passid']}', '{$val['uId']}', '{$val['uName']}', '{$val['add_time']}')";
}

echo 'INSERT INTO liuyan_comment(`comment`, `zhuanti_id`, `lvl`, `sts`, `passid`, `uId`, `uName`, `add_time`) VALUES' . implode(',',
        $sql);
